mapper.select:
      	SELECT a.cartId, a.detailId, a.loginid, a.amount, b.brand, b.detailName, b.price, b.discountPer, b.img
		FROM tb_cart a , tb_productdetail b
		WHERE 	a.detailId IN 

        	#{detailId}

		AND a.detailId = b.detailId;

mapper.update:
UPDATE tb_order
SET		paymentMethod = #{paymentMethod},
		creaditInfo = #{creaditInfo},
		phone = #{phone},
		address = #{address},
		addDetail = #{addDetail},
		zip = #{zip},
		location = #{location}
WHERE	orderId = #{orderId}
AND loginid = #{loginId}

mapper.delete:
		DELETE
		FROM 	tb_order
		WHERE 	orderId IN 

        	#{orderId}

		AND loginid = #{loginId}

mapper.select:
      SELECT a.orderId, a.detailID, a.loginid, a.totPayment, a.status, a.paymentDt, b.brand, b.detailName, b.img
	  FROM tb_order a, tb_productdetail b
	  WHERE a.loginid = #{loginId}
	  AND a.detailId = b.detailId

		and a.paymentDt BETWEEN #{startDate} AND #{endDate}

	  ORDER by a.paymentDt asc
