mapper.insert:

            SELECT max(CAST(s_no  as unsigned)) + 1   FROM tb_schedule

		INSERT
		INTO		tb_schedule
				(
					s_no, loginID,  s_contents, s_date, s_regdate
				)
		VALUES
				(
						#{s_no}
					,	#{loginId}
					,	#{s_contents}
					,	#{s_date}
					,	NOW()
				)


            SELECT max(CAST(d_no  as unsigned)) + 1 
   FROM tb_diary

		INSERT
		INTO		tb_diary
				(
					d_no, loginID, d_title, d_weather, d_mood, d_contents, d_diarydt, d_regdate
				)
		VALUES
				(
						#{d_no}
					,	#{loginId}
					,	#{d_title}
					,	#{d_weather}
					,	#{d_mood}
					,	#{d_contents}
					,	#{d_diarydt}
					,	NOW()
				)
